CREATE DATABASE training;


CREATE EXTERNAL DATA SOURCE BikeShareStorage
WITH(
    LOCATION='https://practicestoragemilgard.blob.core.windows.net/training/raw/bike_share/2017-capitalbikeshare-tripdata/'
);

SELECT TOP 100 *
FROM OPENROWSET(
    BULK '*',
    DATA_SOURCE='BikeShareStorage',
    FORMAT = 'CSV',
    PARSER_VERSION = '2.0',
    HEADER_ROW = TRUE
) AS [result];
